import{_ as a,o as r,c as d,a as h,d as e,e as t,f as n}from"./app-631283dc.js";const i={},c=e("div",{class:"hint-container danger"},[e("p",{class:"hint-container-title"},"重要章节"),e("p",null,"🎥 这是重要章节，尤其是第二节。")],-1),s=e("h1",{id:"第七章-电视场面调度",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#第七章-电视场面调度","aria-hidden":"true"},"#"),t(" 第七章 电视场面调度")],-1),o=e("p",null,"🎥 这是重要章节，尤其是第二节。电视场面调度是对拍摄现场中的人物和镜头的总体设计和安排。即电视场面调度包括人物调度和镜头调度两个方面。电视场面人物调度不同与电影、电视剧及文艺节目的调度，在电视节目制作中经常碰到的人物调度是主持人的站位、行走路线的设计、出境记者在拍摄现场的选位、采访路线的排等；镜头调度是指摄像师运用不同的拍摄方向(正拍、侧拍、背拍)、不同的拍摄角度(俯拍、平拍、仰拍)、不同的拍摄景别(远、全、中、近、特)、不同的镜头运动(推、拉、摇、移、跟、升降并包括固定镜头)，获得不同的视角、不同的视距、不同视域的画面，表现所拍的内容和作者的意图。",-1),_=n('<h2 id="第一节-电视场面调度" tabindex="-1"><a class="header-anchor" href="#第一节-电视场面调度" aria-hidden="true">#</a> 第一节 电视场面调度</h2><h3 id="电视场面调度特点" tabindex="-1"><a class="header-anchor" href="#电视场面调度特点" aria-hidden="true">#</a> 电视场面调度特点</h3><h3 id="电视场面调度作用" tabindex="-1"><a class="header-anchor" href="#电视场面调度作用" aria-hidden="true">#</a> 电视场面调度作用</h3><p>①丰富画面语言和造型形式，增强电视画面的概括力和艺术表现力。<br> ②渲染环境气氛，通过场面调度创造特定的情景和艺术效果。<br> ③场面调度可以通过一系列不同角度、不同景别的画面，作为蒙太奇镜头表现被摄人物活动的情景和局部细节，并经由这些画面的组接形成人物活动及事件过程的完整印象。<br> ④有助于对节目：节奏的把握，有助于形成画面的节奏变化。<br> ⑤场面调度有助于刻画人物性格，揭示人物的内心活动。<br> ⑥通过运动摄像的场面调度方法，有助于形成长镜头纪实性拍摄。<br> ⑦在现代的大型运动会、综艺节日等的转播制作过程中，统筹有序的场面调度是极其重要的工作环节之一。</p><h2 id="第二节-电视场面调度例说" tabindex="-1"><a class="header-anchor" href="#第二节-电视场面调度例说" aria-hidden="true">#</a> 第二节 电视场面调度例说</h2><h3 id="轴线问题" tabindex="-1"><a class="header-anchor" href="#轴线问题" aria-hidden="true">#</a> 轴线问题</h3><h3 id="三角形原理" tabindex="-1"><a class="header-anchor" href="#三角形原理" aria-hidden="true">#</a> 三角形原理</h3>',7);function l(f,u){return r(),d("div",null,[c,s,o,h(" more "),_])}const x=a(i,[["render",l],["__file","第七章 电视场面调度.html.vue"]]);export{x as default};
