import{_ as a,r as p,o as l,c as r,a as i,d as s,e as t,b as e,f as o}from"./app-631283dc.js";const d={},c=s("h1",{id:"第八章-艺术风格、流派、思潮",tabindex:"-1"},[s("a",{class:"header-anchor",href:"#第八章-艺术风格、流派、思潮","aria-hidden":"true"},"#"),t(" 第八章 艺术风格、流派、思潮")],-1),h=s("p",null,"重点艺术风格考的比较多。",-1),_=s("h2",{id:"第一节-艺术风格",tabindex:"-1"},[s("a",{class:"header-anchor",href:"#第一节-艺术风格","aria-hidden":"true"},"#"),t(" 第一节 艺术风格")],-1),y={style:{"text-align":"justify"}},b=s("span",{id:"艺术风格",style:{color:"#f1ca17"}},[s("b",null,"艺术风格")],-1),g=s("br",null,null,-1),f={id:"一、艺术风格的界定",tabindex:"-1"},u=s("a",{class:"header-anchor",href:"#一、艺术风格的界定","aria-hidden":"true"},"#",-1),x={style:{"text-align":"justify"}},v=s("span",{style:{color:"#43b244"}},"（一） 艺术风格的特征与体现",-1),m=s("p",null,[s("span",{style:{color:"#126bae"}},[s("b",null,"艺术家的风格是艺术家本人的精神风貌，思想人格在作品中的具体体现。")]),t(" 18世纪法国评论家布封在他的《论风格》一文中提到“风格即人”的著名论点。 艺术风格，正式创作主体思想审美意识的表现方式是艺术家本人的印记和标志。"),s("span",{style:{color:"#f1ca17"}},[s("b",null,"艺术风格，是艺术家个人创造精神和聪明才智的结晶。")]),t("只有那些有着较高才智,有着敏锐的艺术感染力和丰富的艺术想象力以及孜孜不倦锐意创新的艺术家，才有可能在创作实践中逐步形成自己的风格。")],-1),j=s("p",null,[s("span",{style:{color:"#126bae"}},[s("b",null,"艺术风格是欣赏者认识艺术家的重要依据。")]),t("一般来说,风格往往会通过主题意念的开掘，题材的选择，结构的安排等方面体现出来。鲜明独具的艺术特色 会给欣赏者带来有的魅力，召唤欣赏者产生浓的兴趣，并在欣赏的过程中逐渐 熟悉艺术家的特点，加深对它的理解和认识。")],-1),B=s("p",null,[s("span",{style:{color:"#126bae"}},[s("b",null,"艺术风格还是美学理论、艺术理论的重要研究对象。")]),t("由于风格的存在与发展，深刻地体现了艺术的内在规律，因而他在很早就成为艺术理论家关注的重要方面。")],-1),N=s("span",{id:"艺术风格形成原因",style:{color:"#43b244"}},[s("b",null,"（二） 艺术风格的形成原因（重点）")],-1),V=o('<p>艺术风格的形成有着复杂的多方面的原因，它受到艺术家自身各种 条件及社会政治、经济、文化等各方面的制约与影响。归纳为主观的与客观的两个方面。</p><p><span style="color:#ba2f7b;"><b>1、主观因素</b></span></p><p>它主要是指作为创作主体的艺术家的自身条件心理功能。主要是指艺术家的情感、气质及其它心理特质。审美情感是审美主体对审美对象的一种主观体验和感受，其内容是丰富的，由于审美主体的心理差异，便使审美情感具有很强的主观色彩和个性特点。<span style="color:#f79646;">比如冯小刚导演的冯氏幽默，就源自于他的心理特征。</span></p><p><span style="color:#43b244;">生活经历。</span>艺术家个人生活经历的不同，会对他的创作风格产生较大的影响。<span style="color:#f79646;">例如高尔基的自传体小说三部曲，就是以作者本人青少年时期的亲身经历为素材，也是那段饱受苦难的亲身生活经历，使他形成独特的创作风格。</span></p><p><span style="color:#43b244;">思想修养。</span>即世界观因素。艺术家在哲学，政治，道德，美学等方面的修养和观点无不渗透于作品之中。作品总要表达艺术家的思想和精神风貌，没有思想内蕴的作品是难以想象的。</p><p><span style="color:#43b244;">艺术修养。</span>艺术家的多方面的艺术修养，如艺术的鉴赏力和艺术的感受生活的能力，艺术表现能力也对风格的形成起重要作用。一个人在他学习艺术的过程中所直接受到的教育和熏陶，对他一生创作风格的形成，都会产生积极的影响。</p><p><span style="color:#ba2f7b;"><b>2、客观因素</b></span></p><p>风格是在艺术家的主观特性与对客观现实的真实反应相结合中表现出来的时代及社会因素。艺术家生活的外部时代条件和社会环境会对他们风格的形成和变化产生较大的影响。<span style="color:#f79646;">例如导演贾樟柯，出生于山西汾阳，他在县城生活和在京城求学以追求自我价值实现的过程中，感知了历史转型时期人们的迷茫、困惑和无所适从。这给了他很大的影响，以至于成为导演以后，他开始以电影为媒介来考察当代都市普通或边缘人的生活状态，用镜头语言讲述人在时代浪潮中的变化，关注着生命的卑微与伟大。从而形成了自己独特的风格。</span></p><p><span style="color:#43b244;">民族因素。</span>艺术家从属于一定的民族，在一定的民族区域中生活。那些民族特定的社会生活，文化传统心理素质，精神状态，风土人情，审美理想就会反映到创作中来，<span style="color:#f79646;">例如《大红灯笼高高挂》正是在独特的民族特色背景下创作的。</span></p><p><span style="color:#43b244;">文化因素。</span>这种文化因素是指在一定的地域和环境中的文化状况，文化氛围及艺术的形式因素等，艺术家风格的形成和变异与特定的文化状况，文化氛围，有着十分直接的关系。<span style="color:#f79646;">例如纵观中国历史，从古至今，在书画曲艺等领域有所建树之人，无一不有丰富的中华传统文化底蕴，如梅兰竹菊是书画家竟相创作的对象，其共同特点是淡泊名利，自强不息，正符合传统文化中的君子之道。</span></p>',10),k=s("span",{style:{color:"#f1ca17"}},[s("b",null,"第六种无意识类型：集体无意识")],-1),w=o('<p><span style="color:#43b244;">定义：</span>集体无意识是瑞士心理学家荣格提出来的，他将无意识分为处于表层的个体无意识和处于深层的集体无意识两层，认为集体无意识的意义远远大于个体无意识。集体无意识是从人的祖先继承下来的原始经验的总和，是与生俱来的，凭借代传，蓄积在每个个体的心灵深处，成为种族的心理积淀。其内容是通过遗传继承人类原始意象的某种基本模式或结构。</p><p><span style="color:#43b244;">分类：</span> 集体无意识包含着两个方面的内容，</p><p><span style="color:#126bae;"><b>一是人类生理进化方面的本能集体无意识。</b></span>人类五官和大脑神经生理上为了适应集体劳动和集体生活的需要而产生的潜在的本能的共同的心理活动，也是构成集体无意识的生理基础。</p><p><span style="color:#126bae;"><b>二是民族文化历史发展中形成的集体无意识。</b></span>是一个民族在长期的文化历史发展过程中，逐渐形成传统的民族性格和民族精神中最富有凝聚力的共同具有的潜在的民族心理，即民族的集体无意识。</p><p>集体无意识在艺术家身上体现的十分突出，因为艺术家对民族文化、历史发展的知识，经过系统的学习后了解和继承的最多，其中对集体无意识的继承自然也是相当丰富的。越是伟大的艺术家，越是对他自己民族的民族精神和民族性格所积淀起来的集体无意识表现得越充分。</p>',5),C={id:"二、艺术风格的类别",tabindex:"-1"},D=s("a",{class:"header-anchor",href:"#二、艺术风格的类别","aria-hidden":"true"},"#",-1),q=s("strong",null,"二、艺术风格的类别",-1),E=o('<div style="text-align:justify;"><p>艺术风格的类别有多种，一般来看，认识艺术风格的类别可以从三个维度来理解。</p><p><span style="color:#43b244;">第一，依据艺术风格的表现主体及其题材来划分</span>，艺术风格可以分为艺术家风格，艺术流派风格和艺术作品风格三种。<strong>艺术家风格</strong>是指在艺术家审美精神，创造意识和艺术特色等方面体现出来的特点。<strong>艺术流派风格</strong>是指那些获得社会共识的艺术流派所拥有的共同的艺术特色。一个成熟的艺术流派，其成员在风格上必然会有相近之处，而风格的相近正是艺术家们在艺术观念，审美理想，创作方法等方面一致的表现。<strong>艺术作品风格</strong>是指作品在内容与形式的和谐统一中所展现出来的总的思想倾向和艺术特色。集中体现为在主题的提炼，题材的选择，形象的塑造，题材的驾驭艺术，语言和艺术手法的运用等方面。</p><p><span style="color:#43b244;">第二，依据艺术作品呈现的美学特征来划分</span>，可以产生较多划分方式，将艺术风格分为多种类别。例如，按照<strong>西方美学</strong>的一般模式来划分，可以将艺术风格分为优美，崇高，悲剧，喜剧，滑稽，荒诞等类别。按照<strong>中国传统美学</strong>来划分，可以将艺术风格划分为豪放，沉郁，悲慨，俊爽，冲淡等。</p><p><span style="color:#43b244;">第三，依据艺术活动及其作品在社会历史进程中呈现出来的特色划分</span>，可以将艺术风格分为<strong>时代风格</strong>，<strong>民族风格</strong>和<strong>地域风格</strong>。</p><p><strong>时代风格</strong>是指在特定时期所形成的具有浓郁时代风貌和人文内涵的艺术风格。</p><p><strong>民族风格</strong>是指在每个民族艺术发展进程中所呈现出来的艺术风格，每个民族的艺术均与自身的历史演变和社会发展相伴随，形成了属于自己民族的艺术特色。艺术家对民族精神的坚守和对民族理想的追求，抒发着民族的情怀，是该民族文化传承的重要表征。</p><p><strong>地域风格</strong>是指在一定的地域和环境中呈现出来的艺术活动及其作品的风格。地域风格一方面与一定的地理，自然气候等因素相联系，更多则属于文化的范畴。当各种地域风貌与文化特征相互融合，文化及艺术的演变会逐渐出现独有的轨迹，显现出别具风釆的审美文化品格。</p></div>',1),S={id:"三、艺术风格的特点及其意义",tabindex:"-1"},T=s("a",{class:"header-anchor",href:"#三、艺术风格的特点及其意义","aria-hidden":"true"},"#",-1),z=s("strong",null,"三、艺术风格的特点及其意义",-1),A=o('<h4 id="一-艺术风格的基本特点" tabindex="-1"><a class="header-anchor" href="#一-艺术风格的基本特点" aria-hidden="true">#</a> （一）艺术风格的基本特点</h4><div style="text-align:justify;"><p><span style="color:#43b244;"><b>1、继承性与独创性</b></span></p><p><strong>艺术的风格具有继承性。</strong> 无论任何人都不能无视前人艺术成就的客观存在，都要在本民族或其他民族已经形成的某些艺术风格的基础上。继承和发展风格的继承性，除此之外，还需要艺术家对前人的艺术进行相互渗透与交融，吸收与借鉴前人的艺术特色和元素，并不断的完善从而形成属于自己的创作风格。</p><p><strong>同样艺术风格也具有独创性。</strong> 无论是艺术家或者作品的风格，还是民族时代，地域或者流派的风格，均是对前人艺术风格的继承与弘扬，同时也必然凝结着人们的创造精神和独具的个性意识。</p><p><strong>因此，艺术风格不仅要有继承性，而且要有独创性。</strong> 正如人类艺术活动的发展需要继承，同时也需要创新一样，艺术风格的存在与发展需要有继承性与独创性。<span style="color:#f79646;">例如张艺谋电影《影》中即继承了中国传统的“水墨美学”展现了整场的水墨配色和连绵不断的雨，同时也独创了一种独特的打斗方式:竹竿对战竹伞的打斗场景。</span></p><p><span style="color:#43b244;"><b>2、稳定性与变异性</b></span></p><p><strong>艺术风格的相对稳定性是指艺术家特定的风格特点一旦形成，就会贯穿于创作的各个阶段和各个方面，表现出一定的连续性和稳定性。</strong> 这种相对的稳定性和艺术家的情感，性格，气质等密切相关，民族文化传统和审美心理的继承性，也是形成艺术风格相对稳定性的重要因素。<span style="color:#f79646;">例如作为香港的先锐导演，王家卫的电影自有其独到的风格并长期稳定。他总爱用一幅墨镜来掩饰他内心的情绪一样，王家卫的电影爱用支离破碎的画面来掩饰他电影中所要表现的内在，所以看王家卫的电影，不单是观赏他独特的画面处理方式，你还得透过支离破碎破碎的画面去把握其中深蕴的主题。因而，王家卫的电影多了几分耐人寻味。</span></p><p><strong>艺术风格的变异性体现在艺术家在长期的生活实践和艺术实践中，会使世界观，艺术素养，审美意识不断发展变化。</strong> 即使属于较为稳定的心理因素等诸多方面也不会凝固不变。这些方面的变异会促使和引导着艺术风格的变化。在客观方面，社会生活，时代精神，艺术思潮的发生变化会对艺术家的思想及审美意识产生影响，从而使风格发生一定变化。<span style="color:#f79646;">例如，由于受到经济全球化的影响，各个国家的文化相互沟通与交流，在我国逐渐出现了二次元文化街头文化，说唱文化，街舞文化，宅文化等等。</span></p><p><span style="color:#43b244;"><b>3、多样性与同一性</b></span></p><p><strong>艺术风格的多样化是艺术风格的必然特征，艺术表现客体世界的多样性。</strong> 艺术家思想，情感生活，经验，审美理想，创作才能的多样性，欣赏者对艺术的需要和爱好的多样性规定了艺术风格的多样性。不同时代，不同民族，不同地域的艺术家，其风格是千差万别的，即使是同一时代，同一民族的艺术家，其风格也各不相同。<span style="color:#f79646;">例如画马，唐代韩干画的马则有熊健丰满的风骨，宋代李公麟画马，则有骏健不凡的风貌，现代徐悲鸿画的马则表现出刚健奔放的风格。</span></p><p><strong>艺术风格的同一性。</strong> 就独立的艺术家而言，是指他的风格尽管多样，但总会保持自己一贯的稳定的主导性风格，同时也是指艺术家的创作风格尽管多样，但不能脱离和超出时代民族的风格而独立存在。<span style="color:#f79646;">例如张艺谋电影中的红色和韵律美是始终作为张艺谋电影的一个基本主题，这是任何一个熟悉他电影的人所共同感受到的。他对完美形式的追求在当代电影艺术中是绝无仅有的，在艺术风格上始终保持着同一性。</span></p></div>',2),F={id:"二-倡导艺术风格丰富性和多样性发展的意义",tabindex:"-1"},G=s("a",{class:"header-anchor",href:"#二-倡导艺术风格丰富性和多样性发展的意义","aria-hidden":"true"},"#",-1),H=o('<div style="text-align:justify;"><p><span style="color:#43b244;">倡导艺术风格的丰富性和多样性发展是繁荣艺术创作的必由之路。</span>没有艺术风格的多样化，就不会带来艺术的繁荣，只有提倡艺术风格的多样性，这样才能够持续地为人民大众提供更多丰富的艺术产品，满足人民大众不断增长的审美文化需求。</p><p><span style="color:#43b244;">倡导艺术风格的丰富性和多样性发展是驱动艺术创新的根本途径。</span>艺术风格的多样化发展，符合艺术创作的基本要求是对艺术本质规律的深化和自觉遵循。是艺术的不断创新。没有风格的多样化呈现，势必会造成艺术创作的呆滞、千篇一律，不利于艺术创作。</p><p><span style="color:#43b244;">倡导艺术风格丰富性和多样性发展，是使中国艺术走向世界的重要举措。</span>在国际文化交流及其文化艺术的市场贸易中，一方面需要充分展现出中国文化的悠久，厚重的博大精深，同时也应当以缤纷多彩的艺术特色和当代气息显示出强大的市场竞争力和影响力，只有这样才能弘扬中国传统文化，增强中国文化软实力</p></div><h2 id="第二节-艺术流派" tabindex="-1"><a class="header-anchor" href="#第二节-艺术流派" aria-hidden="true">#</a> 第二节 艺术流派</h2><h3 id="一、艺术流派的形成" tabindex="-1"><a class="header-anchor" href="#一、艺术流派的形成" aria-hidden="true">#</a> 一、艺术流派的形成</h3>',3),I={style:{"text-align":"justify"}},J=s("span",{style:{color:"#43b244"}},[s("b",null,"（一）艺术流派的形成")],-1),K=o('<p>艺术流派是指由思想倾向，审美观念，艺术趣味，创作风格相近或相似的一些艺术家组成的艺术派别。</p><p><strong>1、具有相对稳定的艺术创作群体,包括具有卓越创造能力的创始人和一定数量的代表人物。</strong><span style="color:#f79646;">例如我国的“民间派”便是由一批业余或非专业的美术作者、出版工作者、美术编辑及民间艺人等组成，而这也是各博客圈子人数众多的一个庞大的群体。</span></p><p><strong>2、具有传承性。</strong> 一般来说，凡是具有较大影响的艺术流派，均是以前人的艺术成就为基础，继承，吸纳和不断创新的结果，从而又对后世产生了较大影响的风格特色。<span style="color:#f79646;">例如我国新派京剧对于旧派京剧的传承，理念是能够更多地保留和体现流派的原生态的同时，也做到对其本身的创新。</span></p><p><strong>3、具有鲜明的艺术观念,创作宗旨以及代表性艺术作品。</strong> 这是形成艺术流派的基础条件。<span style="color:#f79646;">例如印象派，因莫奈的画作《印象-日出》而得名。它的艺术观念便是抛弃了古典艺术“绘画中最重要的就是线条”的告诫，着重于展现光影的改变而否定轮廓。</span></p><p><strong>4、同一流派的人们有着相近或相似的艺术表现风格。</strong> 正是在美学观念相通的基础上，逐渐形成了比较相近的创作方法，艺术风格和艺术表现手段。<span style="color:#f79646;">例如建安文学，建安作家用自己的笔直抒胸襟，抒发渴望建功立业的雄心壮志，掀起了我国诗歌史上文人创作的第一个高潮。由于其时正是汉献帝建安年代，故后世称为建安文学，也称建安风骨。</span></p><p><strong>5、有较稳定的受众群体。</strong> 正是由于拥有较为广泛的受众，方能形成流派赖以生存的成长和传承的土壤。受众的参与和推波助澜有助于增进流派走向兴盛。<span style="color:#f79646;">例如电影艺术就拥有最广泛驳杂的受众群体，因而他的受众群体稳定，由此可以通过它的传播，能形成一种公共话语空间。</span></p>',6),L=s("span",{style:{color:"#43b244"}},[s("b",null,"（二）艺术流派的形成方式，一般有几种形态（形成方式）")],-1),M=o('<p>艺术流派是指在中外艺术一定历史时期里，由一批思想倾向、美学主张、创作方法和表现风格方面相似或相近的艺术家们所形成的艺术派别。艺术流派有以下三种类型：</p><p><span style="color:#126bae;"><b>第一种是自觉组成的艺术社团。</b></span>即由一些志同道合的艺术家群体在长期创作中有意倡导、自觉组织起来的艺术组织，他们有大体相近的哲学基础和美学观点，以及相近的艺术趣味和艺术风格，会将自己的主张或艺术纲领公之于众，形成具有自觉意识的艺术流派。<span style="color:#f79646;">当他们取得了一定实绩，在社会上产生了较大影响的时如达达派。20 世纪西方现代主义文艺流派之一的达达主义，就是法籍罗马尼亚诗人查拉和其他几位年轻诗人与艺术家于 1916 年在瑞士苏黎世形成。</span></p><p><span style="color:#126bae;"><b>第二种是在不自觉的境况下自然形成的艺术流派。</b></span>此类艺术家大都属于艺术创作的题材、技巧、理论主张比较相近，或者生活于共同地区，是在艺术发展过程中自然形成的流派。但是往往没有形成专门的组织以及明确的纲领或艺术宣言，是一种在不自觉的境况下自然而然形成的艺术流派，具有比较松散的特征。<span style="color:#f79646;">例如左岸派。电影中的“左岸派&quot;是 1960 年前后发过新浪潮电影运动中的一个流派，是以导演阿仑•雷乃为首的一批在艺术上有某些共同点的艺术家们形成，这一流派的重要作品包括《广岛之恋》、《去年在马里昂巴德》等。</span></p><p><span style="color:#126bae;"><b>第三种是由他人或后人认定的艺术流派。</b></span>在一定时期，有的艺术家提出和实践了某种艺术主张或创作原则，逐渐得到其他一些艺术家的赞同和效仿，形成风气，并得到社会的承认，但未必真正具有实质性联络或形成结社性群体，而是得到他人或后世人们的认可。<span style="color:#f79646;">例如我中国文学史上著名的“建安文学”，当时并没有形成明确或固定的艺术流派，只是由于汉末魏初时期特殊的社会历史环境，以曹氏父子和建安七子为代表的一批文学家，以慷慨悲壮的情感和刚健明快的语言，写出来一大批以“风骨”著称的文学作品。</span></p>',4),O=o('<h3 id="二、艺术流派流变" tabindex="-1"><a class="header-anchor" href="#二、艺术流派流变" aria-hidden="true">#</a> 二、艺术流派流变</h3><div style="text-align:justify;"><p>艺术在发展进程中由于社会状况的复杂性和影响力，以及艺术活动内部规律的趋势及其演变的复杂性与多样性。致使艺术家或时代民族的风格均呈现出独有的特点。正是这种独具特色的艺术创作与艺术作品。随身着艺术流派的生存。一般来说，艺术流派的原曲萌生和成熟均有着一定的过程，显现出特有的轨迹，其<strong>发生流变的原因</strong>也有多种如下：</p><p><strong>艺术流派的形成与流变，均需要适宜的土壤。</strong> 具有大体相同的审美倾向和艺术风格的艺术家会促成某一流派的形成，同时也离不开社会政治经济文化等因素的促动与影响。</p><p><strong>艺术流派的流变与发展，更重要的还是在于艺术活动内部众多艺术家艺术群体之间的相互碰撞，融合与影响</strong>。特别是作为某些组织者的出现具有关键性的作用。</p><p><strong>艺术流派中，同一流派中的某些艺术家可能会表现出不尽相同的审美倾向和艺术风格。</strong> 这种同种有意的现象正是艺术风格丰富性和多样性的体现。有的艺术流派可以持续较长的时期，体现出一定的继承性和延续性。也有一些艺术流派只存在于一个较短的时期，经历了短暂的活跃之后便走向衰微。</p></div><h3 id="三、艺术流派影响" tabindex="-1"><a class="header-anchor" href="#三、艺术流派影响" aria-hidden="true">#</a> 三、艺术流派影响</h3><div style="text-align:justify;"><p>在艺术发展史上，由于各时期不同艺术流派的兴盛与竞争，充满了不同艺术主张、艺术特色相互渗透、相互影响、相互作用的现象，推动着各门类艺术走向兴旺和繁盛。</p><p><strong>流派的形成有助于激活艺术领域内部的创新活力。</strong> 人们将一方面努力坚持继承和维护自身的艺术风格和流派特征，另一方面又要坚持创新，使得流派风格在新的时期得到拓展和光大。流派的形成有利于不同特色艺术群体之间的相互交流与共同发展。不同流派之间的沟通、切磋，甚至论争，可以形成不同艺术观念之间的互渗与互融，实现艺术理论层次的提升、艺术境界的超越，以及艺术语言、艺术形式的创新。同时又要鼓励艺术家及其流派之间在学术思想、艺术理念、创作方法等方面的相互碰撞与论争。</p><p><strong>艺术流派之间的论争与竞争，对于繁荣艺术创作、激活艺术生产，为社会和大众提供丰富和优质的艺术产品，具有重要的作用。</strong> <span style="color:#f79646;">例如，各流派之间通过竞争不断探索更优的表达方式，从而进一步完善了其流派的艺术创作与艺术主张。</span></p></div><h2 id="第三节-艺术思潮" tabindex="-1"><a class="header-anchor" href="#第三节-艺术思潮" aria-hidden="true">#</a> 第三节 艺术思潮</h2><h3 id="一、艺术思潮的产生-条件" tabindex="-1"><a class="header-anchor" href="#一、艺术思潮的产生-条件" aria-hidden="true">#</a> 一、艺术思潮的产生（条件）</h3><div style="text-align:justify;"><p><span style="color:#43b244;">艺术思潮是指在一定社会条件下，由于受到一定的社会思潮和哲学思潮的影响，艺术领域所出现的具有较大影响的思想潮流和创作倾向。</span>艺术风格是创作主体独特个性的表现，艺术流派是风格相近或相似的创作主体的群体化，而艺术思潮则是倡导某种文艺思想的几个或多个艺术流派所形成的一种艺术潮流。</p><p><span style="color:#43b244;">艺术思潮是特定历史和时代的产物，是在特有的社会思潮与哲学思潮的影响下出现的现象。</span>一定的艺术思潮又与一定的哲学思潮有着紧密的联系,艺术思 潮的本质通常以一定的哲学思潮为基础，蕴含着深刻的哲学内涵，没有哲学思潮 及其美学思潮为基础，艺术思潮难以形成潮流以及产生积极的影响。</p><p><span style="color:#43b244;">艺术思潮的出现，更重要的则是源于艺术内在规律的驱动。</span>是一定的社会和历史时期艺术活动与发展的结果。艺术有其自身的规律和特性,分析艺术思潮 这一现象，既要重视各种外在的因素和影响，更要注重对其内在自身规律的探讨。</p></div><h3 id="二、艺术思潮的类型" tabindex="-1"><a class="header-anchor" href="#二、艺术思潮的类型" aria-hidden="true">#</a> 二、艺术思潮的类型</h3><div style="text-align:justify;"><p>历史上出现的各种艺术思潮，有的影响较大，有的则影响较小，总的来讲，艺术思潮可以分为以下三种主要类型。</p><p><span style="color:#43b244;">第一，以？？的艺术思潮。</span>此类艺术思潮与社会和时代的进程紧紧相连，将人的思想解放与社会变革为目标社会变革作为艺术探索的根本目标。艺术思潮对于感召大众、宣传社会主张、深人进行思想启蒙和理论普及，均具有重要的意义。例如欧洲文艺复兴时代，艺术思潮的蓬勃兴起和不断蔓延，对于欧洲多国人民大众的思想觉醒，起到了不可磨灭的作用。</p><p><span style="color:#43b244;">第二，以文化创新为宗旨的艺术思潮。</span>第该类思潮以鲜明的文化创新及其艺术精神的深化作为基本宗旨。<span style="color:#f79646;">例如中国古代不同时期出现的艺术思潮，如魏晋玄学思潮、唐代古文运动、明清性灵说等文艺传统等均对其所处时期的文化变革持续地产生影响。</span></p><p><span style="color:#43b244;">第三，以艺术形式创新为主旨的艺术思潮。</span>该类艺术思潮中再突出艺术的形式，语言和方式方法的研究与探索。努力使各门类艺术在其形式上有较大的创新和突破。科学技术的因素在其间发挥着重要的作用，近年来电影界出现的对 3D 电影的创作热潮即是如此。<span style="color:#f79646;">以《阿凡达》为代表的 3D 电影的成功推进了电影语言和电影表现形式的创新丰富和强化了电影思维，增强了电影的表现力。</span></p></div><h3 id="三、艺术思潮的评述-影响-意义" tabindex="-1"><a class="header-anchor" href="#三、艺术思潮的评述-影响-意义" aria-hidden="true">#</a> 三、艺术思潮的评述（影响，意义）</h3><div style="text-align:justify;"><p>艺术思潮是社会和文化发展综合因素的呈现。它通常会对艺术发展产生较大的作用力，是艺术活动和文化活动中各种因素剧烈运动的结果。</p><p><strong>艺术思潮可以影响和推进艺术创新的重大突破和迅捷发展。</strong> 如果出现一定艺术思潮的涌动，人们会加大艺术内容创新和形式语言变革的力度；而当艺术思潮进入全盛时期，将会激励人们的创新能力急剧爆发，例如兴起于 20 世纪 20 年代末、兴盛于 30 年代的左翼文艺运动，不仅在文学方面，而且在音乐、美术、戏剧、电影等方面出现了全面的变革，既表现于内容的升华,也表现于形式方面的革新，创造了突出的成就。</p><p><strong>艺术思潮具有突出的流动性和交叉性。</strong> 当一种艺术思潮在某地兴起后，就会以极快的速度迅速蔓延。19 世纪欧洲的现实主义思潮和浪漫主义思潮的蔓延，均具有多向流动的特点。其交叉性，即指在特定的时期，一定的艺术思潮在其运行与蔓延的过程中，会与其他思潮形成汇合与交融，甚至出现多样性艺术思潮的重新组合。例如 20 世纪西方现代艺术运动，就充分显现出各种不同艺术思潮的交汇与相互影响，呈现十分复杂的特点。</p><p><strong>艺术思潮有时也可能参与和影响社会及时代的变革，作用于社会发展。</strong> 艺术的行变从来也不是被动地接受社会政治、经济、科技、文化的推动，而是通常以兴起艺术思潮或运动的方式，积极地融人社会，参与社会的变革和发展。艺术在一定时期可能会对社会的发展呈现积极的推动作用。主要是通过作用于人的精神来实现的。</p><p><strong>艺术思潮并非全都体现为积极的和正面的作用，有时某些艺术思潮可能会出现不够积极甚至消极的形态，与社会及文化发展相游离，甚至相背反。</strong> 20 世纪西方现代主义思潮，表现出人们对西方社会内部矛盾的焦虑、无奈与种种困惑，呈现消极和负面的能量。</p></div>',11);function P(Q,R){const n=p("Badge");return l(),r("div",null,[c,h,i(" more "),_,s("div",y,[s("p",null,[t("🌻 "),b,t(),e(n,{text:"名词解释",type:"warning",vertical:"middle"}),t(),e(n,{text:"2011",type:"info",vertical:"middle"}),g,t(" 主要是指艺术家的创作个性与艺术作品所呈现出的相对稳定的艺术风貌、特色、作风、格调和气派。他是艺术家鲜明独特的创作个性的体现，统一于艺术作品的内容与形式，思想与艺术之中。风格是艺术家创造个性成熟的标志，也是作品达到较高艺术水准的标志，能体现出一个艺 术家在全部作品中反映的出来的基本特色和创作个性。")])]),s("h3",f,[u,t(" 一、艺术风格的界定 "),e(n,{text:"简答",type:"tip",vertical:"middle"})]),s("div",x,[s("p",null,[v,t(),e(n,{text:"简答",type:"tip",vertical:"middle"})]),m,j,B,s("p",null,[N,t(),e(n,{text:"简答",type:"tip",vertical:"middle"}),e(n,{text:"2012",type:"info",vertical:"middle"})]),V,s("p",null,[t("🌻 "),k,t(),e(n,{text:"名词解释",type:"warning",vertical:"middle"})]),w]),s("h3",C,[D,t(),q,t(),e(n,{text:"简答",type:"tip",vertical:"middle"})]),E,s("h3",S,[T,t(),z,t(),e(n,{text:"简答",type:"tip",vertical:"middle"})]),A,s("h4",F,[G,t(" （二）倡导艺术风格丰富性和多样性发展的意义"),e(n,{text:"简答",type:"tip",vertical:"middle"})]),H,s("div",I,[s("p",null,[J,t(),e(n,{text:"简答",type:"tip",vertical:"middle"})]),K,s("p",null,[L,t(),e(n,{text:"简答",type:"tip",vertical:"middle"})]),M]),O])}const W=a(d,[["render",P],["__file","第八章 艺术风格、流派、思潮.html.vue"]]);export{W as default};
